// HTML content for the landing page
const html = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>AI-Powered Language Learning</title>
    <style>
        body {
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif;
            margin: 0;
            padding: 0;
            background-color: #f4f7f6;
            color: #333;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
        }
        .container {
            background-color: #ffffff;
            padding: 40px;
            border-radius: 12px;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
            text-align: center;
            max-width: 800px;
            width: 90%;
        }
        h1 {
            color: #2c3e50;
            margin-bottom: 30px;
            font-size: 2.5em;
        }
        .options-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
            gap: 30px;
            margin-top: 30px;
        }
        .option-card {
            background-color: #f8f9fa;
            padding: 30px;
            border-radius: 8px;
            border: 1px solid #e9ecef;
            transition: transform 0.3s ease, box-shadow 0.3s ease;
        }
        .option-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 8px 20px rgba(0, 0, 0, 0.12);
        }
        .option-card h2 {
            margin-top: 0;
            color: #3498db;
            font-size: 1.5em;
            margin-bottom: 15px;
        }
        .option-card p {
            color: #555;
            font-size: 1em;
            line-height: 1.6;
            margin-bottom: 25px;
        }
        .option-card .icon {
            font-size: 3em; /* Adjust icon size */
            margin-bottom: 15px;
            display: block;
        }
        .btn {
            display: inline-block;
            background-color: #3498db;
            color: white;
            padding: 12px 25px;
            border: none;
            border-radius: 5px;
            text-decoration: none;
            font-size: 1em;
            font-weight: bold;
            transition: background-color 0.3s ease;
        }
        .btn:hover {
            background-color: #2980b9;
        }
        .btn-song { background-color: #2ecc71; }
        .btn-song:hover { background-color: #27ae60; }
        .btn-vocab { background-color: #e74c3c; }
        .btn-vocab:hover { background-color: #c0392b; }

    </style>
</head>
<body>
    <div class="container">
        <h1>AI-Powered Language Learning</h1>
        <p>Choose an activity to start learning!</p>

        <div class="options-grid">
            <div class="option-card">
                <span class="icon">üéµ</span>
                <h2>Song Learning App</h2>
                <p>Learn vocabulary and phrases by analyzing the lyrics of your favorite songs.</p>
                <a href="/webhook/song-learning" class="btn btn-song">Start Song Learning</a>
            </div>

            <div class="option-card">
                <span class="icon">üñºÔ∏èüß†</span>
                <h2>Vocabulary Image Game</h2>
                <p>Test your knowledge by guessing the word represented by an AI-generated image.</p>
                <a href="/webhook/vocabulary-game" class="btn btn-vocab">Play Vocab Game</a>
            </div>
        </div>
    </div>
</body>
</html>`;

// Convert HTML to base64 for binary data
const base64Data = Buffer.from(html).toString('base64');

// Return in binary format for the webhook
return [{
  binary: {
    data: {
      data: base64Data,
      mimeType: 'text/html'
    }
  }
}];
